CREATE TABLE `mydb`.`aircrafts` ( `id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(20) NOT NULL , `model` VARCHAR(50) NOT NULL , `business_class_capacity` INT NOT NULL , `econom_class_capacity` INT NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `mydb`.`cities` ( `id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(50) NOT NULL , `country` VARCHAR(50) NOT NULL , `short_name` VARCHAR(10) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `mydb`.`flights` ( `id` INT NOT NULL , `aircraft_id` INT NOT NULL , `departure_city_id` INT NOT NULL , `arrival_city_id` INT NOT NULL , `departure_time` VARCHAR(100) NOT NULL , `econom_place_price` INT NOT NULL , `business_place_price` INT NOT NULL ) ENGINE = InnoDB;
CREATE TABLE `mydb`.`tickets` ( `id` INT NOT NULL AUTO_INCREMENT , `flight_id` INT NOT NULL , `name` VARCHAR(20) NOT NULL , `surname` VARCHAR(20) NOT NULL , `passport_number` VARCHAR(50) NOT NULL , `ticket_type` VARCHAR(20) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
